import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import 'dotenv/config';

import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import fs from "fs/promises";
import path from "path";
import { storage } from "./storage";
import { insertUserSchema } from "@shared/schema";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  try {
    // Ensure required directories exist
    await fs.mkdir(path.join(process.cwd(), "uploads"), { recursive: true });
    await fs.mkdir(path.join(process.cwd(), "reports"), { recursive: true });
  } catch (e) {
    log(`failed to ensure directories: ${String(e)}`);
  }

  // Seed default users if they do not exist yet
  try {
    const existingAdmin = await storage.getUserByCredentials({ userId: "admin1", password: "admin" });
    const existingDoctor = await storage.getUserByCredentials({ userId: "doctor1", password: "doctor" });
    const existingKeeper = await storage.getUserByCredentials({ userId: "keeper1", password: "keeper" });

    if (!existingAdmin) {
      await storage.createUser(insertUserSchema.parse({ userId: "admin1", password: "admin", role: "admin", name: "Admin User", email: "admin@example.com" }));
      log("seeded default admin user (admin1/admin)");
    }
    if (!existingDoctor) {
      await storage.createUser(insertUserSchema.parse({ userId: "doctor1", password: "doctor", role: "doctor", name: "Doctor User", email: "doctor@example.com" }));
      log("seeded default doctor user (doctor1/doctor)");
    }
    if (!existingKeeper) {
      await storage.createUser(insertUserSchema.parse({ userId: "keeper1", password: "keeper", role: "zookeeper", name: "Keeper One", email: "keeper@example.com" }));
      log("seeded default zookeeper user (keeper1/keeper)");
    }
  } catch (e) {
    log(`user seeding skipped/failed: ${String(e)}`);
  }

  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",

  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request } from "express";
import { createServer, type Server } from "http";
import session from "express-session";
import { storage } from "./storage";
import { loginSchema, insertCommentSchema } from "@shared/schema";
import multer from "multer";
import { exec } from "child_process";
import { promisify } from "util";
import fs from "fs/promises";
import path from "path";

// Extend Express Request to include session
declare module "express-serve-static-core" {
  interface Request {
    session: any;
  }
}

const execAsync = promisify(exec);

// Configure multer for audio uploads
const upload = multer({
  dest: "uploads/",
  limits: { fileSize: 50 * 1024 * 1024 }, // 50MB limit
  fileFilter: (req, file, cb) => {
    if (file.mimetype.startsWith("audio/")) {
      cb(null, true);
    } else {
      cb(new Error("Only audio files are allowed"));
    }
  },
});

// Session middleware for authentication
const requireAuth = (req: Request, res: any, next: any) => {
  if (!req.session?.user) {
    return res.status(401).json({ message: "Authentication required" });
  }
  next();
};

const requireRole = (roles: string[]) => (req: Request, res: any, next: any) => {
  if (!roles.includes(req.session?.user?.role)) {
    return res.status(403).json({ message: "Insufficient permissions" });
  }
  next();
};

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup session middleware
  app.use(session({
    secret: process.env.SESSION_SECRET || "zoo-management-secret",
    resave: false,
    saveUninitialized: false,
    cookie: { 
      secure: false, // Set to true in production with HTTPS
      maxAge: 24 * 60 * 60 * 1000 // 24 hours
    }
  }));

  // Authentication routes
  app.post("/api/auth/login", async (req, res) => {
    try {
      const credentials = loginSchema.parse(req.body);
      const user = await storage.getUserByCredentials(credentials);

      if (!user) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      req.session.user = user;
      res.json({ user: { id: user.id, userId: user.userId, role: user.role, name: user.name } });
    } catch (error) {
      console.error("Login error:", error);
      res.status(400).json({ message: "Invalid request" });
    }
  });

  app.post("/api/auth/logout", (req, res) => {
    req.session.destroy((err: any) => {
      if (err) {
        return res.status(500).json({ message: "Could not log out" });
      }
      res.json({ message: "Logged out successfully" });
    });
  });

  app.get("/api/auth/user", requireAuth, (req: Request, res) => {
    const user = req.session.user;
    res.json({ id: user.id, userId: user.userId, role: user.role, name: user.name });
  });

  // Audio processing route for zoo keepers
  app.post("/api/submissions/audio", 
    requireAuth, 
    requireRole(["zookeeper"]), 
    upload.single("audio"), 
    async (req: any, res) => {
      try {
        if (!req.file) {
          return res.status(400).json({ message: "Audio file is required" });
        }

        const { date } = req.body;
        if (!date) {
          return res.status(400).json({ message: "Date is required" });
        }

        const user = req.session.user;
        const audioPath = req.file.path;
        const uploadedMime = req.file.mimetype || "audio/wav";
        const ext = uploadedMime.split("/")[1] || "wav";
        const audioFileName = `${user.userId}_${date}_${Date.now()}.${ext}`;

        try {
          // Read audio file
          const audioBuffer = await fs.readFile(audioPath);

          // Process audio using Python zoo model
          const pythonScript = path.join(process.cwd(), "server", "zoo_model.py");
          const tempAudioPath = path.join(process.cwd(), "uploads", audioFileName);

          // Copy audio to temp location for processing
          await fs.copyFile(audioPath, tempAudioPath);

          // Call Python script to process audio (cross-platform)
          const runPy = process.env.PYTHON || (process.platform === 'win32' ? 'python' : 'python3');
          const runnerPath = path.join(process.cwd(), "server", "run_model.py");
          const { stdout, stderr } = await execAsync(`${runPy} "${runnerPath}" "${tempAudioPath}" "${date}" hi "${uploadedMime}"`);

          if (stderr) {
            console.error("Python script error:", stderr);
          }

          const structuredData = JSON.parse(stdout.trim());

          // Generate TXT file
          const txtContent = generateTxtReport(structuredData, user.name, date);
          const txtFileName = `${user.userId}_${date}_${Date.now()}.txt`;
          const txtPath = path.join(process.cwd(), "reports", txtFileName);

          // Ensure reports directory exists
          await fs.mkdir(path.join(process.cwd(), "reports"), { recursive: true });
          await fs.writeFile(txtPath, txtContent);

          // Store in database
          const submission = await storage.createSubmission({
            userId: user.id,
            date,
            audioFileName,
            transcription: stdout.includes("transcript") ? "Transcribed from Hindi audio" : undefined,
            structuredData,
            txtFileName,
            status: "processed",
          });

          // Clean up temp files
          await fs.unlink(audioPath);
          await fs.unlink(tempAudioPath);

          res.json({ 
            message: "Audio processed successfully", 
            submissionId: submission.id,
            structuredData 
          });

        } catch (processingError) {
          console.error("Audio processing error:", processingError);

          // Fallback processing
          const fallbackData = {
            date_or_day: date,
            animal_observed_on_time: true,
            clean_drinking_water_provided: true,
            enclosure_cleaned_properly: true,
            normal_behaviour_status: true,
            normal_behaviour_details: null,
            feed_and_supplements_available: true,
            feed_given_as_prescribed: true,
            other_animal_requirements: "Audio processing error - manual review required",
            incharge_signature: user.name,
            daily_animal_health_monitoring: `Observation recorded on ${date} - Audio processing encountered an error`,
            carnivorous_animal_feeding_chart: "Standard feeding schedule followed",
            medicine_stock_register: "Stock levels adequate",
            daily_wildlife_monitoring: `Wildlife monitoring completed on ${date}`
          };

          const txtContent = generateTxtReport(fallbackData, user.name, date);
          const txtFileName = `${user.userId}_${date}_${Date.now()}_fallback.txt`;
          const txtPath = path.join(process.cwd(), "reports", txtFileName);

          await fs.mkdir(path.join(process.cwd(), "reports"), { recursive: true });
          await fs.writeFile(txtPath, txtContent);

          const submission = await storage.createSubmission({
            userId: user.id,
            date,
            audioFileName,
            transcription: "Audio processing failed",
            structuredData: fallbackData,
            txtFileName,
            status: "processed",
          });

          res.json({ 
            message: "Audio processed with fallback data", 
            submissionId: submission.id,
            structuredData: fallbackData 
          });
        }

      } catch (error) {
        console.error("Submission error:", error);
        res.status(500).json({ message: "Failed to process audio submission" });
      }
    }
  );

  // Get submissions for current user (zoo keeper)
  app.get("/api/submissions/my", requireAuth, requireRole(["zookeeper"]), async (req: any, res) => {
    try {
      const submissions = await storage.getSubmissionsByUser(req.session.user.id);
      res.json(submissions);
    } catch (error) {
      console.error("Error fetching user submissions:", error);
      res.status(500).json({ message: "Failed to fetch submissions" });
    }
  });

  // Get all submissions (admin and doctor)
  app.get("/api/submissions/all", requireAuth, requireRole(["admin", "doctor"]), async (req, res) => {
    try {
      const submissions = await storage.getAllSubmissions();
      res.json(submissions);
    } catch (error) {
      console.error("Error fetching all submissions:", error);
      res.status(500).json({ message: "Failed to fetch submissions" });
    }
  });

  // Get submission details with comments
  app.get("/api/submissions/:id", requireAuth, async (req, res) => {
    try {
      const submissionId = parseInt(req.params.id);
      const submission = await storage.getSubmissionById(submissionId);

      if (!submission) {
        return res.status(404).json({ message: "Submission not found" });
      }

      res.json(submission);
    } catch (error) {
      console.error("Error fetching submission:", error);
      res.status(500).json({ message: "Failed to fetch submission" });
    }
  });

  // Add comment to submission (admin and doctor)
  app.post("/api/submissions/:id/comments", 
    requireAuth, 
    requireRole(["admin", "doctor"]), 
    async (req: any, res) => {
      try {
        const submissionId = parseInt(req.params.id);
        const { content } = insertCommentSchema.parse(req.body);

        const comment = await storage.createComment({
          submissionId,
          userId: req.session.user.id,
          content,
        });

        res.json(comment);
      } catch (error) {
        console.error("Error adding comment:", error);
        res.status(400).json({ message: "Failed to add comment" });
      }
    }
  );

  // Download TXT report
  app.get("/api/submissions/:id/download", requireAuth, async (req, res) => {
    try {
      const submissionId = parseInt(req.params.id);
      const submission = await storage.getSubmissionById(submissionId);

      if (!submission || !submission.txtFileName) {
        return res.status(404).json({ message: "Report not found" });
      }

      const filePath = path.join(process.cwd(), "reports", submission.txtFileName);
      res.download(filePath, submission.txtFileName);
    } catch (error) {
      console.error("Error downloading report:", error);
      res.status(500).json({ message: "Failed to download report" });
    }
  });

  // Update submission (zoo keeper can edit their own)
  app.put("/api/submissions/:id", requireAuth, async (req: any, res) => {
    try {
      const submissionId = parseInt(req.params.id);
      const submission = await storage.getSubmissionById(submissionId);

      if (!submission) {
        return res.status(404).json({ message: "Submission not found" });
      }

      // Check permissions
      const user = req.session.user;
      if (user.role === "zookeeper" && submission.userId !== user.id) {
        return res.status(403).json({ message: "Can only edit your own submissions" });
      }

      const { structuredData } = req.body;

      // Regenerate TXT file with updated data
      if (structuredData) {
        const txtContent = generateTxtReport(structuredData, submission.user.name, submission.date);
        const txtPath = path.join(process.cwd(), "reports", submission.txtFileName!);
        await fs.writeFile(txtPath, txtContent);
      }

      const updated = await storage.updateSubmission(submissionId, {
        structuredData: structuredData || submission.structuredData,
      });

      res.json(updated);
    } catch (error) {
      console.error("Error updating submission:", error);
      res.status(500).json({ message: "Failed to update submission" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

function generateTxtReport(data: any, keeperName: string, date: string): string {
  return `
ZOO ANIMAL MONITORING REPORT
============================

Date: ${date}
Zoo Keeper: ${keeperName}
Generated: ${new Date().toLocaleString()}

OBSERVATION DETAILS
-------------------
Animal Observed on Time: ${data.animal_observed_on_time ? "Yes" : "No"}
Clean Drinking Water Provided: ${data.clean_drinking_water_provided ? "Yes" : "No"}
Enclosure Cleaned Properly: ${data.enclosure_cleaned_properly ? "Yes" : "No"}
Normal Behaviour Status: ${data.normal_behaviour_status ? "Yes" : "No"}
${data.normal_behaviour_details ? `Behaviour Details: ${data.normal_behaviour_details}` : ""}
Feed and Supplements Available: ${data.feed_and_supplements_available ? "Yes" : "No"}
Feed Given as Prescribed: ${data.feed_given_as_prescribed ? "Yes" : "No"}
${data.other_animal_requirements ? `Other Requirements: ${data.other_animal_requirements}` : ""}

MONITORING SUMMARIES
--------------------
Daily Animal Health Monitoring:
${data.daily_animal_health_monitoring}

Carnivorous Animal Feeding Chart:
${data.carnivorous_animal_feeding_chart}

Medicine Stock Register:
${data.medicine_stock_register}

Daily Wildlife Monitoring:
${data.daily_wildlife_monitoring}

AUTHORIZATION
-------------
In-charge Signature: ${data.incharge_signature}

---
This report was generated automatically by the Zoo Management System.
  `.trim();
}

server/storage.ts.content:
import {
  users,
  submissions,
  comments,
  type User,
  type InsertUser,
  type Submission,
  type InsertSubmission,
  type Comment,
  type InsertComment,
  type SubmissionWithUser,
  type SubmissionWithComments,
  type LoginCredentials,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and } from "drizzle-orm";

export interface IStorage {
  // User operations
  getUserByCredentials(credentials: LoginCredentials): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  getUserById(id: string): Promise<User | undefined>;

  // Submission operations
  createSubmission(submission: InsertSubmission & { userId: string }): Promise<Submission>;
  getSubmissionsByUser(userId: string): Promise<SubmissionWithUser[]>;
  getAllSubmissions(): Promise<SubmissionWithUser[]>;
  getSubmissionById(id: number): Promise<SubmissionWithComments | undefined>;
  updateSubmission(id: number, updates: Partial<InsertSubmission>): Promise<Submission>;

  // Comment operations
  createComment(comment: InsertComment & { userId: string }): Promise<Comment>;
  getCommentsBySubmission(submissionId: number): Promise<(Comment & { user: User })[]>;
}

export class DatabaseStorage implements IStorage {
  async getUserByCredentials(credentials: LoginCredentials): Promise<User | undefined> {
    const [user] = await db
      .select()
      .from(users)
      .where(
        and(
          eq(users.userId, credentials.userId),
          eq(users.password, credentials.password)
        )
      );
    return user;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async getUserById(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async createSubmission(submission: InsertSubmission & { userId: string }): Promise<Submission> {
    const [newSubmission] = await db
      .insert(submissions)
      .values(submission)
      .returning();
    return newSubmission;
  }

  async getSubmissionsByUser(userId: string): Promise<SubmissionWithUser[]> {
    const result = await db
      .select()
      .from(submissions)
      .leftJoin(users, eq(submissions.userId, users.id))
      .where(eq(submissions.userId, userId))
      .orderBy(desc(submissions.createdAt));

    return result.map(row => ({
      ...row.submissions,
      user: row.users!,
    }));
  }

  async getAllSubmissions(): Promise<SubmissionWithUser[]> {
    const result = await db
      .select()
      .from(submissions)
      .leftJoin(users, eq(submissions.userId, users.id))
      .orderBy(desc(submissions.createdAt));

    return result.map(row => ({
      ...row.submissions,
      user: row.users!,
    }));
  }

  async getSubmissionById(id: number): Promise<SubmissionWithComments | undefined> {
    const [submission] = await db
      .select()
      .from(submissions)
      .leftJoin(users, eq(submissions.userId, users.id))
      .where(eq(submissions.id, id));

    if (!submission) return undefined;

    const submissionComments = await db
      .select()
      .from(comments)
      .leftJoin(users, eq(comments.userId, users.id))
      .where(eq(comments.submissionId, id))
      .orderBy(desc(comments.createdAt));

    return {
      ...submission.submissions,
      user: submission.users!,
      comments: submissionComments.map(c => ({
        ...c.comments,
        user: c.users!,
      })),
    } as SubmissionWithComments;
  }

  async updateSubmission(id: number, updates: Partial<InsertSubmission>): Promise<Submission> {
    const [updated] = await db
      .update(submissions)
      .set({ ...updates, updatedAt: new Date() })
      .where(eq(submissions.id, id))
      .returning();
    return updated;
  }

  async createComment(comment: InsertComment & { userId: string }): Promise<Comment> {
    const [newComment] = await db
      .insert(comments)
      .values(comment)
      .returning();
    return newComment;
  }

  async getCommentsBySubmission(submissionId: number): Promise<(Comment & { user: User })[]> {
    const result = await db
      .select()
      .from(comments)
      .leftJoin(users, eq(comments.userId, users.id))
      .where(eq(comments.submissionId, submissionId))
      .orderBy(desc(comments.createdAt));

    return result.map(r => ({
      ...r.comments,
      user: r.users!,
    }));
  }
}

export const storage = new DatabaseStorage();

