                <span className="text-primary font-medium">Excellent</span>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Submissions Table */}
        <Card className="shadow-lg border border-border">
          <CardHeader>
            <div className="flex items-center justify-between">
