                  <SelectContent>
                    <SelectItem value="all">All Keepers</SelectItem>
                    {uniqueKeepers.map((keeper) => (
                      <SelectItem key={keeper.id} value={keeper.id}>
                        {keeper.name}
                      </SelectItem>
                    ))}
